package com.crypto.analysis.main.fundamental.stock;import com.crypto.analysis.main.data_utils.select.StaticData;import com.crypto.analysis.main.data_utils.select.coin.TimeFrame;import com.crypto.analysis.main.data_utils.select.fundamental.FundamentalStock;import com.crypto.analysis.main.data_utils.select.fundamental.FundamentalTimeFrame;import com.crypto.analysis.main.vo.CandleObject;import com.crypto.analysis.main.vo.FundamentalStockObject;import com.fasterxml.jackson.databind.JsonNode;import okhttp3.Request;import okhttp3.Response;import java.io.IOException;import java.text.ParseException;import java.util.*;import static com.crypto.analysis.main.data_utils.select.StaticData.objectMapper;import static com.crypto.analysis.main.data_utils.select.StaticData.sdfFullISO;public class FundamentalDataUtil {    private final FundamentalTimeFrame interval;    private final int capacity;    private TreeMap<Date, Double> SPX;    private TreeMap<Date, Double> DXY;    private TreeMap<Date, Double> DJI;    private TreeMap<Date, Double> VIX;    private TreeMap<Date, Double> NDX;    private TreeMap<Date, Double> GOLD;    public FundamentalDataUtil(int capacity, TimeFrame interval) {        this.interval = TimeFrameConverter.convert(interval);        this.capacity = capacity;        init();    }    private void init() {        SPX = getData(FundamentalStock.SPX, capacity, interval);        DXY = getData(FundamentalStock.DXY, capacity, interval);        DJI = getData(FundamentalStock.DJI, capacity, interval);        VIX = getData(FundamentalStock.VIX, capacity, interval);        NDX = getData(FundamentalStock.NDX, capacity, interval);        GOLD = getData(FundamentalStock.GOLD, capacity, interval);    }    public FundamentalStockObject getFundamentalData(CandleObject coinCandle) {        FundamentalStockObject result = new FundamentalStockObject(interval);        result.setSPX(SPX.floorEntry(coinCandle.getOpenTime()).getValue());        result.setDXY(DXY.floorEntry(coinCandle.getOpenTime()).getValue());        result.setDJI(DJI.floorEntry(coinCandle.getOpenTime()).getValue());        result.setVIX(VIX.floorEntry(coinCandle.getOpenTime()).getValue());        result.setNDX(NDX.floorEntry(coinCandle.getOpenTime()).getValue());        result.setGOLD(GOLD.floorEntry(coinCandle.getOpenTime()).getValue());        return result;    }    public static TreeMap<Date,Double> getData(FundamentalStock symbol, int capacity, FundamentalTimeFrame interval) {        if (interval==FundamentalTimeFrame.ONE_HOUR) {            capacity*=2;            interval = FundamentalTimeFrame.THIRTY_MINUTES;        }        capacity+=10;        if (capacity > 5000) throw new IllegalArgumentException("Count capacity must be less than 5000");        String url = String.format("https://api.twelvedata.com/time_series?symbol=%s&timezone=UTC&outputsize=%s&interval=%s&apikey=%s",                symbol.getName(), capacity, interval.getTimeFrame(), StaticData.twelveDataKey);        Request request = new Request.Builder()                .url(url)                .build();        TreeMap<Date, Double> result = new TreeMap<>();        try {            Response response = StaticData.okHttpClient.newCall(request).execute();            if (response.isSuccessful()) {                assert response.body() != null;                String jsonData = response.body().string();                JsonNode root = objectMapper.readTree(jsonData);                JsonNode valuesNode = root.get("values");                for (JsonNode node : valuesNode) {                    Date openTime = sdfFullISO.parse(node.get("datetime").asText());                    double open = node.get("open").asDouble();                    result.put(openTime, open);                }            } else {                System.out.println("Error: " + response.code() + " " + response.message());            }            response.close();        } catch (IOException | ParseException e) {            e.printStackTrace(System.out);        }        return result;    }}